import React, { useEffect, useState } from 'react'

import { ListData } from "../asset/components/RanderList/ListData";
import axios from 'axios';
import { fetchDataMovie, useMovieDataQuery } from '../services/movie/get-data-movie';

export const HomePage = () => {

const [movies, setMovies] = useState([]);

const [PageNow, setPageNow] = useState(1);

//level 3
// const{data:fetchUser, refetch: loadUlang} = useMovieDataQuery(PageNow)

//level 4 (lebih dinamais menmapung banyak parameter dengan sederhana)
const{data:fetchUser} = useMovieDataQuery({
  language : "en-US",
  page : PageNow
})

//security level 1 hanya menambah file .env
// const options = {
//   method: 'GET',
//   //core
//   url: `${process.env.REACT_APP_SERVER}3/movie/now_playing`,
//   params: {language: 'en-US', page: '1'},
//   headers: {
//     accept: 'application/json',
//     Authorization: `Bearer ${process.env.REACT_APP_KEY}`
//   }
// };

//dengan then and catch
// axios.request(options)
//   .then(function (response) {
//     setMovies(response.data.results);
//     console.log(response.data.results)
//   })
//   .catch(function (error) {
//     console.error(error);
//   });

//dengan async
// const getDataMovie = async()=>{
//   const data = await axios.request(options)
// } 

//security level 2
// const getDataMovie = async () => {
//   const data = await fetchDataMovie()
//   setMovies(data.results) 
//   // console.log(movies, "data movie");
// } 

//level 3
const getDataMovie = async () => {
  // mengirim parameter PageNOW
  // const data = await fetchDataMovie(PageNow)
  setMovies(data.results) 
  // console.log(movies, "data movie");
} 



//untuk melakukan tindakan saat pertama dijalankan atau di mounting
useEffect(()=>{
  getDataMovie()
  console.log(fetchUser, "ini datanya")
}, [])


  return (

   
    <div>
      <div className="flex min-h-screen flex-col mt-10 bg-gray-100 gap-4 rounded">
          <div className='flex items-center justify-center '>
            <h1 className=' mt-1 text-5xl font-bold justify-center flex'>List Film</h1>
          </div>
          <div className="bg-white p-6 mx-4 rounded-lg shadow-md">
            <h1 className="text-3xl font-semibold mb-4">Movie Search</h1>
            <div className="flex">
              <input type="text" className="w-full py-2 px-3 rounded-l-md focus:outline-none" placeholder="Search for movies..."/>
              <button className="bg-blue-500 text-white py-2 px-4 rounded-r-md hover:bg-blue-600 focus:outline-none">
                Search
              </button>
            </div>
          </div>

            <div className="mx-auto px-4">
              <h1 className="text-3xl font-semibold my-6 justify-center flex">Now Playing Movies</h1>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {console.log(movies, "data movie")}
                {movies.map(movie => (
                  <ListData key={movie.id} title={movie.title} releaseDate={movie.release_date}posterPath={movie.poster_path} />
                ))}
                
              </div>
              <h1>{PageNow}</h1>
              <button onClick={()=>{
                setPageNow(PageNow + 1)
              }}>Next Page</button>

            </div>
      </div>

       
    </div>
  )
}
