          </div>
          <div>
             <input type="text" className="bg-transparent border-2 border-red-600 w-[30rem] py-2 px-3 rounded-full focus:outline-none" placeholder="Search for movies..."/>
          </div>
          <div>
            <button class="text-red mr-4">Login</button>
            <button class="text-white">Register</button>
          </div>
        </div>
          
              <div>
                <h1>{PageNow}</h1>
                <button onClick={()=>{
                  setPageNow(PageNow + 1)
                }}>Next Page</button>

              </div>

      </div>
       
    </div>
  )
}
