import React, { useEffect, useState } from 'react'
import { useCreateUser, RegisterUser } from '../../services/auth/register_user'
export const RegisterPage = () => {
  
    // const [Movie, setMovie] = useState([])
    const [Username, setUsername] = useState()
    const [Password, setPassword] = useState()
    const [Email, setEmail] = useState() 

    const{mutate: RUser, isSuccess, error} = useCreateUser()

    const handleInput=(e)=>{
        if(e){
            if(e.target.id === "username"){
                setUsername(e.target.value)
            }
            if(e.target.id === "email"){
                setEmail(e.target.value)
            }
            if(e.target.id === "password"){
                setPassword(e.target.value)
            }
        }
        
    }

    if (error){
        console.log (error.response.data.message, "ini error")
    }

    const registerUser=()=>{
        RUser({
            "email" : Email,
            "name" : Username,
            "password" : Password 
        })
    }


    return (
   
    <div>
        <input onChange={handleInput} id="username" type="text" className='border' placeholder='username' />
        <input onChange={handleInput} id="email" type="text" className='border' placeholder='email' />
        <input onChange={handleInput} id="password" type="text" className='border' placeholder='password'/>
        <button onClick={()=>{registerUser()}}> Register </button>
        {/* {console.log(Movie)} */}
    </div>
  )
}
