import React, { useState } from "react";

export const ListData = ({ data, key }) => {
  const [check, setCheck] = useState(data.complete)
  let [Data, setData] = useState(data)
  let [task, setTask]= useState(data.task) 
  let [edit, setEdit] = useState(true)

  const handleCheckboxChange = () => {
    setCheck(!check);
  }

//  let deleteTask=(key, data)=>{
    
//     console.log(key+"in data")
//     const updatedData = data.filter((value) => value.key !== key){

//         setData(updatedData);

//     }
  
  // Mengatur kembali data dengan array yang telah diperbarui
  

//  }

const deleteTask = () => {
    // Menghapus elemen dari daftar data berdasarkan task
    const updatedData = Data.filter((item) => item.task !== task);
    // Mengatur kembali data dengan array yang telah diperbarui
    setData(updatedData);
  };

  return (
    <div className="px-4 py-2 flex justify-between gap-4 items-center bg-yellow-500 rounded w-full">
      <div>
        <p className={`${check ? "line-through" : ""}`}>
            {edit?<span>{task}</span>:<input value={task} onChange={(e)=>{setTask(e.target.value)}} className="w-[25rem] h-[2rem]"/>}
            {console.log(setTask)}
        </p>
        {console.log(check)}
      </div>
      <div className="flex w-[12%] bg-green-500 justify-between">
        <input checked={check} onChange={handleCheckboxChange} type="checkbox" />
        <button onClick={()=>{setEdit(!edit)}}>edit</button>
        <button onClick={()=>{deleteTask()}}>hapus</button>
      </div>
    </div>
  );
};
