import React, { useState } from "react";

export const ListData = ({ task, check }) => {
  const [line, setLine] = useState(check);

  const handleCheckboxChange = () => {
    setLine(!line);
  };

  return (
    <div className="px-4 py-2 flex justify-between gap-4 items-center bg-yellow-500 rounded w-full">
      <div>
        <p className={`${line ? "line-through" : ""}`}>{task}</p>
      </div>
      <div className="w-[10%] justify-between">
        <input checked={line} onChange={handleCheckboxChange} type="checkbox" />
        <button>edit</button>
        <button>hapus</button>
      </div>
    </div>
  );
};
