import React, { useState } from 'react'
import { location, state} from 'react-router-dom'

export const AddList = (props) => {
    let data = props.Data;
    let tes="aku"

    let [InputData, setInputData] = useState("")

    function generateRandomId() {
        return Math.floor(Math.random() * 1) + 1;
    }

    const Submit=()=>{
        const dataBaru = InputData
        let dataAwal=data

        dataAwal.push({
            "id": generateRandomId(),
            "task": data,
            "complete": false
        })
    }
    // console.log(data)

  return (
    <div>
        <h1 className=' mt-6 text-2xl font-bold justify-center flex'>TodoSearch</h1>
        <div className='m-3 mx-60'>
            <div className='p-3 border-red-500 border-2 rounded-md'>
                <input onChange={(e)=>{setInputData(e.target.value)}} className="w-[100%] h-[2.8rem] rounded mt-2 mb-4 border-2" type='text'/>
                <button  className='w-[100%] bg-red-500 h-[2.8rem] rounded' onClick={()=>{Submit()}} type='submit'>submit</button> 
            </div>
            
        </div>
    </div>
  )
}
