import React, { useState } from 'react'

import { ListData } from "../asset/components/RanderList/ListData";

export const HomePage = () => {

 const [Data, setData] = useState (
  [
    {
      "id": 1,
      "task": "Nyuci mobil",
      "complete": true
    }, 
    {
      "id": 2,
      "task": "Memberi makan kucing",
      "complete": true
    }, 
    {
      "id": 3,
      "task": "Olahraga 10 menit",
      "complete": false
    }, 
    {
      "id": 4,
      "task": "Sarapan sereal",
      "complete": true
    }, 
    {
      "id": 5,
      "task": "Belanja harian",
      "complete": false
    }, 
    {
      "id": 6,
      "task": "Ngeprint tugas",
      "complete": true
    }, 
    {
      "id": 7,
      "task": "Bayar tagihan bulanan",
      "complete": true
    }, 
    {
      "id": 8,
      "task": "Berangkat kuliah",
      "complete": false
    }, 
    {
      "id": 9,
      "task": "Les bahasa Inggris",
      "complete": true
    }, 
    {
      "id": 10,
      "task": "Ke rumah Sabrina",
      "complete": false
    }
  ]
 )
 

  return (

   
    <div>
        
        <h1 className=' mt-6 text-2xl font-bold justify-center flex'>TodoSearch</h1>
        <div className='m-3 mx-60'>
          <div className='p-3 bg-green-300 flex border-red-500 border-2 rounded-md'> 
            <div className='m-4 w-[55%]  '> 
              
              <div className='flex w-[auto] h-[2.5rem] mb-5 bg-red-500 rounded-md'>
                <div className='bg-cyan-400 w-[10%] h-[100%] rounded-l  justify-center items-center flex'>S</div><input className='w-[90%] h-[100%] rounded-r' type="text" />
              </div>
              <button className='bg-red-500 h-[2.8rem] w-[100%] rounded'>Search</button>

            </div>
            <div className='flex w-[45%] m-4 items-end justify-end'>
              <button className='bg-red-500 h-[2.8rem] w-[60%] rounded'>Add New Task</button>
            </div> 
          </div>

          <h1 className=' text-2xl justify-center m-2 font-semibold flex'>TodoLIst</h1>
          <div className='flex justify-between'>
            <button className='bg-red-500 h-[2.8rem] w-[16.6rem] rounded'>All</button>
            <button className='bg-red-500 h-[2.8rem] w-[16.6rem] rounded'>Done</button>
            <button className='bg-red-500 h-[2.8rem] w-[16.6rem] rounded'>Todo</button>
          </div>

          <div className="flex flex-col mt-10 bg-white gap-4 rounded">
            {data.map((data, index) => (
              <ListData key={index} task={data.task} />
            ))}
          </div>
          <div className='flex justify-between mt-8'>
            <button className='bg-red-500 h-[2.8rem] w-[45%] rounded'>Delete done task</button>
            <button className='bg-red-500 h-[2.8rem] w-[45%] rounded'>Delete all task</button>
          </div>
        </div>

        
        
    </div>
  )
}
