import React, { useEffect, useState } from 'react'

import { ListData } from "../asset/components/RanderList/ListData";
import axios from 'axios';
import { fetchDataMovie, useMovieDataQuery } from '../services/movie/get-data-movie';

export const HomePage = () => {

const [movies, setMovies] = useState([]);

const [PageNow, setPageNow] = useState(1);



//level 3
// const{data:fetchUser, refetch: loadUlang} = useMovieDataQuery(PageNow)

//level 4 (lebih dinamais menmapung banyak parameter dengan sederhana)
const{data:fetchUser} = useMovieDataQuery({
  language : "en-US",
  page : PageNow
})

//security level 1 hanya menambah file .env
// const options = {
//   method: 'GET',
//   //core
//   url: `${process.env.REACT_APP_SERVER}3/movie/now_playing`,
//   params: {language: 'en-US', page: '1'},
//   headers: {
//     accept: 'application/json',
//     Authorization: `Bearer ${process.env.REACT_APP_KEY}`
//   }
// };

//dengan then and catch
// axios.request(options)
//   .then(function (response) {
//     setMovies(response.data.results);
//     console.log(response.data.results)
//   })
//   .catch(function (error) {
//     console.error(error);
//   });

//dengan async
// const getDataMovie = async()=>{
//   const data = await axios.request(options)
// } 

//security level 2
// const getDataMovie = async () => {
//   const data = await fetchDataMovie()
//   setMovies(data.results) 
//   // console.log(movies, "data movie");
// } 

//level 3
const getDataMovie = async () => {
  // mengirim parameter PageNOW
  await setMovies(fetchDataMovie.data) 
  // console.log(movies, "data movie");
} 



//untuk melakukan tindakan saat pertama dijalankan atau di mounting
useEffect(()=>{
  // getDataMovie()
  console.log(fetchUser, "ini datanya")
}, [fetchUser])


  return (

   
    <div>
      <div className='m-2 ms-3 font-sans'>
        <div className="min-h-screen bg-transparent bg-orange-600 h-screen">
          <div className='flex justify-between bg-slate-950'>
            <div>
              <h1 className='text-red-500 font-bold text-4xl'>MovieList</h1>
            </div>
            <div>
              <input type="text" className="bg-transparent border-2 border-red-500 w-[30rem] h-[2.5rem] py-2 px-3 rounded-full focus:outline-none" placeholder="Search for movies..."/>
            </div>
            <div className='gap-3'>
              <button class="text-red-500 w-[6rem] h-[2.5rem] mr-4 w-5 rounded-full border-2 border-red-500 font-semibold bg-transparent">Login</button>
              <button class="text-white w-[6rem] h-[2.5rem] rounded-full font-semibold bg-red-500">Register</button>
            </div>
          </div>
        </div>

        <div>
          <div className='flex justify-between'>
            <div>
              <h1 className='text-3xl font-medium'>Popular Movie</h1>
            </div>
            <div>
              <h3>see all movie</h3>
            </div>
          </div>


        </div>

        <div>
          <h1>{PageNow}</h1>
          <button onClick={()=>{
            setPageNow(PageNow + 1)
          }}>Next Page</button>

        </div>
        
      </div>
    </div>
  )
}
