import React, { useState } from 'react'

import { ListData } from "../asset/components/RanderList/ListData";
import axios from 'axios';

export const HomePage = () => {

  
  // let [DataAwal, setDataAwal] = useState([])
const [movies, setMovies] = useState([]);

let [InputData, setInputData] = useState("")

let [DataSearch, setDataSearch] = useState("") 




const options = {
  method: 'GET',
  url: 'https://api.themoviedb.org/3/movie/now_playing',
  params: {language: 'en-US', page: '1'},
  headers: {
    accept: 'application/json',
    Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJhNzExODQwZTlmZGRiMzdmMjA5ZGM1M2MwYzhkYjViMSIsInN1YiI6IjY1MTQxZDk4MDQ5OWYyMDBmZTk4MzVjMiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.Oh2FjKvt5AokjLHm98vR14-DES4iSdQs9pfA7l0KAME'
  }
};

axios.request(options)
  .then(function (response) {
    setMovies(response.data.results);
    console.log(response.data.results)
  })
  .catch(function (error) {
    console.error(error);
  });



  
 

  return (

   
    <div>
        
        
        

        <div className="flex min-h-screen flex-col mt-10 bg-gray-100 gap-4 rounded">
          <div className='flex items-center justify-center '>
            <h1 className=' mt-1 text-5xl font-bold justify-center flex'>List Film</h1>
          </div>
          <div className="bg-white p-6 mx-4 rounded-lg shadow-md">
            <h1 className="text-3xl font-semibold mb-4">Movie Search</h1>
            <div className="flex">
              <input type="text" className="w-full py-2 px-3 rounded-l-md focus:outline-none" placeholder="Search for movies..."/>
              <button className="bg-blue-500 text-white py-2 px-4 rounded-r-md hover:bg-blue-600 focus:outline-none">
                Search
              </button>
            </div>
          </div>

            <div className="mx-auto px-4">
              <h1 className="text-3xl font-semibold my-4">Now Playing Movies</h1>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {movies.map(movie => (
                    <ListData key={movie.id} title={movie.title} releaseDate={movie.release_date}posterPath={movie.poster_path} />
                    ))}
                </div>
            </div>
        </div>

       
    </div>
  )
}
